document.addEventListener("DOMContentLoaded",function(){if(typeof CKEDITOR!="undefined")CKEDITOR.replace("instruction_content",{language:"ru",height:300,toolbar:[["Bold","Italic","Underline","Strike"],["NumberedList","BulletedList"],["Link","Unlink"],["Table"],["Source"]]});window.editInstruction=function(e,t,n,o){document.getElementById("instruction_id").value=e,document.getElementById("instruction_title").value=t,document.querySelector('select[name="category"]').value=n,CKEDITOR.instances.instruction_content&&CKEDITOR.instances.instruction_content.setData(o),document.getElementById("cancelEdit").style.display="inline-block",document.querySelector('button[name="save_instruction"]').textContent="Обновить инструкцию",document.getElementById("instructionForm").scrollIntoView({behavior:"smooth"})};var e=document.getElementById("cancelEdit");e&&e.addEventListener("click",function(){document.getElementById("instruction_id").value="",document.getElementById("instruction_title").value="",document.querySelector('select[name="category"]').value="",CKEDITOR.instances.instruction_content&&CKEDITOR.instances.instruction_content.setData(""),this.style.display="none",document.querySelector('button[name="save_instruction"]').textContent="Сохранить инструкцию"})});